sp_header_footer.client_script:
function ($rootScope, $scope, snRecordWatcher, spUtil, $location, $uibModal, cabrillo, $timeout,$window) {
	var c=this;
	$scope.userID = $scope.user.sys_id;
	$scope.avatarProfile = {
		userID: $scope.user.sys_id,
		name: $scope.user.name,
		initials: $window.NOW.user_initials
	};
	if ($window.NOW.user_avatar) {
		$scope.avatarProfile.userImage = $window.NOW.user_avatar;
	}
	if (cabrillo.isNative())
		$scope.isViewNative = true;

	function popupCenter(url, title, w, h) {
		// Fixes dual-screen position                         Most browsers      Firefox
		var dualScreenLeft = window.screenLeft != undefined ? window.screenLeft : screen.left;
		var dualScreenTop = window.screenTop != undefined ? window.screenTop : screen.top;

		var width = window.innerWidth ? window.innerWidth : document.documentElement.clientWidth ? document.documentElement.clientWidth : screen.width;
		var height = window.innerHeight ? window.innerHeight : document.documentElement.clientHeight ? document.documentElement.clientHeight : screen.height;

		var left = ((width / 2) - (w / 2)) + dualScreenLeft;
		var top = ((height / 2) - (h / 2)) + dualScreenTop;
		var newWindow = window.open(url, title, 'scrollbars=yes, width=' + w + ', height=' + h + ', top=' + top + ', left=' + left);

		// Puts focus on the newWindow
		if (window.focus) {
			newWindow.focus();
		}
		newWindow.onload = function() {
			var jq = newWindow.$j;
			console.log(jq);
			jq('#back_cell').hide();
			jq('#sc_attachment_button').hide();
		};
		return newWindow;
	}

	$scope.openAnonymousChatPopUp = function(evt) {
		$(evt.target).blur();
		var url = "com.glideapp.servicecatalog_cat_item_view.do?v=1&sysparm_id=2ee88dafdb416200f6c270f5bf9619cf&sysparm_preview=true&sysparm_domain_restore=false&sysparm_stack=no&sysparm_queue_id=3a40c019c333120071d07bfaa2d3ae17&sysparm_live_agent_only="+$scope.data.live_agent_only;
		popupCenter (url, "chat_popup", 450, 580);
	};


	$scope.openPopUp = function(evt) {
		$(evt.target).blur();
				var url;
		var queue_id;
		if ($scope.data.queueType == "connect") {
			queue_id = $scope.data.connect_support_queue_id;
		} else {
			queue_id = $scope.data.interaction_queue_id;
		}
		if ($scope.data.live_agent_only) {
			url = "com.glideapp.servicecatalog_cat_item_view.do?v=1&sysparm_id=2bd27963fc241300964fabc2fdf10ea4&sysparm_preview=true&sysparm_domain_restore=false&sysparm_stack=no&sysparm_queue_id="+queue_id;
		} else {
			url = "CustomerServiceChat.do?portal=csp&queue_id="+queue_id+"&user_id="+$scope.userID+"&live_agent_only="+$scope.data.live_agent_only;
		}
		var popup = window.open (url, "chat_popup", "width=450, height=600");
	};

	$scope.openLogin = function () {
		var portal=c.data.portal_url_suffix ;
		var locationPath=$window.location.pathname;
		var locationSearch=$window.location.search;
		var login_page=c.data.portal_login_page;
			$location.url('/'+portal+'?id='+login_page);
		  $scope.$on('$locationChangeStart', function (event, next, current) {
				var nextIsLogin=false;
				var nextUrl=new URL(next);
				nextUrl=nextUrl.search.split('id=')[1];
				if(nextUrl && nextUrl==login_page)
				    nextIsLogin=true;
		     if(!c.data.isLoggedIn && nextIsLogin)
		         $window.sessionStorage.setItem('navBackUri',locationPath+locationSearch);
			});
	/* Redirecting to login page when login link on header is clicked, for the SP login widget to get new look according to STRY50124975
		$scope.modalInstance = $uibModal.open({
			templateUrl: 'csp-modalLogin',
			scope: $scope
		});
		*/
	};

	$rootScope.$on('sp.avatar_changed', function() {
		$scope.userID = "";
		$timeout(function(){
			$scope.userID = $scope.user.sys_id;
		});
	});

	$scope.isHomepage = function() {
		if (!$scope.page.id)
			return true;

		if ($scope.page.id == $scope.portal.homepage_dv)
			return true;

		return false;
	};

}

