sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
   //電話番号の正規表現
   //最初の数字が0以外だとfalse
   //0‐9の半角数字のみで構成されるとfalse
   //8以上14未満の桁数で構成されていなければfalse
    var regex = /^(0[5-9]0[-(]?[0-9]{4}[-)]?[0-9]{4}|0120[-]?\d{1,3}[-]?\d{4}|050[-]?\d{4}[-]?\d{4}|0[1-9][-]?\d{1,4}[-]?\d{1,4}[-]?\d{4})*$/;

    if (g_user.hasRole('workspace_user') && !(g_user.hasRole('admin'))) {
        if (regex.test(newValue)) {
            return true;
        } else {
            g_form.clearValue('u_phonenumber');
            g_form.addErrorMessage('正しい電話番号の形式で入力してください。半角数字のみで入力してください。');
        }
    } 
}
