sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
	//メールアドレスの正規表現
	//記号は[-,.,_]のみ使用可能
	//＠以前に[.]を使用できない
	//英数字と記号のみ使用可能
	//文字数は3文字以上30文字以内
	//最初と最後の文字は英数字のみ使用可能
    var regex = /^[a-z\d][\w.-]*@[\w.-]+\.[a-z\d]+$/i;

    if (g_user.hasRole('workspace_user') && !(g_user.hasRole('admin'))) {
        if (regex.test(newValue)) {
            return;
        } else {
            g_form.clearValue('u_mailaddress');
            g_form.addErrorMessage('正しいメールアドレスの形式で入力してください。英数字と記号のみ使用できます。');

        }
    } 
}
