sys_ui_action.client_script_v2:
function onClick() {
    var actionName = g_form.case_propose_solution();
	g_form.submit(actionName);

}

sys_ui_action.script:
function promptForResolutionInfo() {

	var closeNotes = g_form.getControl('close_notes');
	var resCode = g_form.getControl('resolution_code');

    if ( !closeNotes  ||  !resCode ) {
		getMessage('A solution can’t be proposed because not all required fields are accessible.', function(msg) {
			g_form.addErrorMessage(msg);
		});
		return false;
	}

	var dialog = new GlideModal('sn_csm_gen_ai_case_propose_solution', false, 651, 190);
	getMessage('Propose Solution', function(msg) {
		dialog.setTitle(msg);
	});
	ScriptLoader.getScripts('/scripts/case_accessibility/glide_modal_accessibility.js', function() {
		dialog.template = glideModalTemplate;
		dialog.on('bodyrendered', function(event) {
			glideModalKeyDownHandler(event, dialog.getID());
		});
		dialog.render();
	});
}

if(typeof window == 'undefined')
	proposeSolution();

function proposeSolution() {
	new global.StateFlow().processFlow(current, '8db14a63c3123100d6d210c422d3ae7a', 'manual');
	action.setRedirectURL(current);
}
